scene.onOverlapTile(SpriteKind.Player, sprites.builtin.forestTiles0, function (sprite, location) {
    if (GameEnd == 0) {
        GameEnd = 1
        color.startFadeFromCurrent(color.SteamPunk, 100)
        sprites.destroy(Shotgun)
        sprites.destroy(Menu)
        sprites.destroy(textSprite)
        sprites.destroy(textSprite2)
        sprites.destroy(ammopercent)
        sprites.destroy(Healthpercent)
        GameEnd = 1
        textSprite = textsprite.create("TIME:", 0, 2)
        textSprite.setFlag(SpriteFlag.RelativeToCamera, true)
        textSprite.setPosition(45, 107)
        textSprite2 = textsprite.create("ENEMIES KILLED:", 0, 2)
        textSprite2.setFlag(SpriteFlag.RelativeToCamera, true)
        textSprite2.setPosition(120, 107)
        ammopercent = textsprite.create("20", 0, 2)
        ammopercent.setFlag(SpriteFlag.RelativeToCamera, true)
        ammopercent.setPosition(117, 115)
        Healthpercent = textsprite.create("100%", 0, 2)
        Healthpercent.setFlag(SpriteFlag.RelativeToCamera, true)
        Healthpercent.setPosition(45, 115)
        ammopercent.setText("" + Enemies_Killed)
        Healthpercent.setText("" + Time)
        pause(100)
        timer.after(5000, function () {
            color.startFadeFromCurrent(color.Black)
        })
    }
})
